import { useState, useEffect } from 'react';
import {  Container } from 'react-bootstrap';
import { apiUrl } from '../../utils/variables';
import axios from 'axios';
import { useParams } from 'react-router-dom';


export const AboutUser = () => {

    const [user, setUser] = useState(null);
    const { userId } = useParams();

    useEffect(() => {
        userId && axios.get(`${apiUrl}/users/${userId}`).then((resp) => {
            const userData = resp.data;
            setUser(userData);
        })
    }, [userId])

    return (
        <Container>
            {user && (
                <>
                    <h1>Имя: {user.name}</h1>
                    <span>Ник: {user.username}</span>
                    <span>email: {user.email}</span>
                    <span>адрес: город: {user.address.city} улица: {user.address.street} квартира: {user.address.suite} индекс: {user.address.zipcode}</span>
                    <span>Номер телефона: {user.phone}</span>
                    <span>Вебсайт: {user.website}</span>
                    <span>Компания: {user.company.name}</span>
                </>
            )}
        </Container>
    );
}
