import { combineReducers } from 'redux';
import { postsReducer } from './reducers/posts';
import { commentsReducer } from './reducers/comments';
import { userReducer } from './reducers/user';
import { usersPostsReducer } from './reducers/users-posts';
import { all } from 'redux-saga/effects';
import { watchGetComments } from './actions/comments';
import { watchFetchPosts } from './actions/posts';
import { watchGetUser } from './actions/user';

export const rootReducer = combineReducers({
    posts: postsReducer,
    comments: commentsReducer,
    user: userReducer,
    usersPosts: usersPostsReducer
});


export default function* rootSaga() {
  yield all([
    watchGetComments(),
    watchFetchPosts(),
    watchGetUser()
    // Другие саги
  ]);
}
